                &:nth-child(2n) {
                    color: $slightly_white;
                    -webkit-text-stroke: transparent;

                }
            }
        }
    }
}

.gradient_fade {
    position: absolute;
    width: 70vw;
    height: 100%;
    z-index: 3;
    top: 0;
    right: 0;
    background-image: linear-gradient(to right, rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.6));

}

section .work {
    transition: 0.5s;
    position: absolute;
    top: 0;
    margin: 30vh 0; // Доделать выраниене по центру! учитывать заголовок
    width: 50vw;
    // min-width: 400px;
    height: 40vh;
    // min-height: 200px;
    left: 5vw;
    z-index: 3;

    .artist {
        position: absolute;
        top: 5%;
        left: 3%;
        z-index: 5;
        text-align: left;
        font-size: 3vw;
        transition: 0.5s;
    }

    .project {
        position: absolute;
        bottom: 3%;
        right: 3%;
        z-index: 5;
        text-align: right;
        font-size: 3vw;
        transition: 0.5s;
    }

    .play_btn {
        fill: none;
        stroke: $slightly_white;
        stroke-width: 4px;
        width: 5vw;
        height: 5vw;
        transition: 0.5s;
    }

    .work_cover {
        position: relative;
        display: flex;
        width: 100%;
        height: 100%;
        overflow: hidden;
        &::after{
            position: absolute;
            z-index: 2;
            width:100%;
            height:100%;
            display: block;
            content:"";
            background-color: rgba($color: #000000, $alpha: 0.4);
            transition:0.5s;
        }

        img {
            position: relative;
            // width: 100%;
            object-fit: cover;
            height: auto;
            width: 100%;
            transition: 0.5s;
        }
    }

    &:hover {
        transition: 0.5s;

        .play_btn {
            fill: white;
            stroke: none;
            transition: 0.5s;
        }

        .artist {
            color: transparent;
            transition: 0.5s;
            transform: translate(0, -150%);
            -webkit-text-stroke: 1.5px $slightly_white;
        }

        .project {
            color: transparent;
            transition: 0.5s;
            transform: translate(0, 150%);
            -webkit-text-stroke: 1.5px $slightly_white;
        }

        .work_cover {
            img {
                transform: scale(1.3);
                transition: 0.5s;
            }
             &::after {
                transition:0.5s;
                 background-color: rgba($color: #000000, $alpha: 0.1);
             }
        }
    }
}

.info {
    position: absolute;
    top: 0;
    width: 40vw;
    // min-width: 400px;
    height: 60vh;
    margin: 20vh 2vw 20vh 10vw;
    // min-height: 200px;
    // padding:10px 20px;
    right: 0;
    z-index: 3;

    .buttons_wrap {
        margin: 1vw 0 0 1vw;

        button {
            &:focus {
                outline: $yellow;
            }

            cursor: pointer;
            text-transform: uppercase;
            border-radius: 0;
            border-color: $slightly_white;
            border-width: 0.01vw;
            border-style: solid;
            background-color: transparent;
            color: $slightly_white;
            padding: 0.6vw 1.4vw;
            margin: 0 0.6vw;
            transition: 0.2s;
            letter-spacing: 1.2px;
            font-size:1vw;

            &:hover,
            &.active_btn {
                background-color: $slightly_white;
                color: black;
                transition: 0.2s;


            }
        }
    }

    .about {
        &.active_slide {
            display: block;
        }

        display: none;
        text-transform: uppercase;
        letter-spacing: 1.2px;
        font-size: 1.2rem;
        font-weight: 300;
        line-height: 2rem;
        padding:1vw 0 0 1.6vw;
        color:$slightly_white;
        opacity: 0.7;
    }

    .backstage {
        display: none;
        margin: 1.8vw 0 0 1.6vw;
        overflow: scroll;
        height: 80%;

        .backstage_grid {
            width: 100%;
            height: 100%;
            display: grid;
            grid-template-columns: 30% 30% 30%;
            grid-template-rows: 40%;
            gap: 10px 10px;
            grid-auto-rows: 40%;
            // grid-auto-columns:100px; 
            grid-auto-flow: row;

            img {
                width: 100%;
                height:100%;
                object-fit: cover;
            }

        }

        &.active_slide {
            display: block;
        }

    }

    .credits {
        display: none;
        width: 100%;
        padding:2vw 0 0 1.6vw;
        overflow: scroll;
        height: 80%;

        .credits_grid {
            width: 100%;
            display: grid;
            grid-template-rows: 2rem;
            grid-auto-rows: 2rem;
            grid-template-columns: 12vw auto;
            grid-auto-flow: row;
            gap: 10px 10px;

            h3 {
                font-size: 1.2rem;
                text-transform: uppercase;
                font-weight: 300;

            }

            h2 {
                font-style: italic;
                text-transform: uppercase;
            }
        }

        &.active_slide {
            display: block;
        }
    }
}
